import React, { Component } from "react";
import currencyFormat from "../utils";

export default class Cart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showProceedBtn: false,
      name: "",
      email: "",
      address: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmitOrder = this.handleSubmitOrder.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  }

  handleSubmitOrder = (e) => {
    e.preventDefault();
  };

  render() {
    const { cartItems } = this.props;
    return (
      <div>
        <div className="cart">
          {cartItems.length === 0 ? (
            <div>You have empty items added</div>
          ) : (
            <div>You have added {cartItems.length} in the cart</div>
          )}
        </div>
        <div className="main-cart">
          <ul className="cart-item">
            {cartItems.map((item) => {
              return (
                <li key={item._id}>
                  <div>
                    <img src={item.image} alt={item.title} />
                  </div>
                  <div>
                    <div>{item.title}</div>
                    <div className="toRight">
                      {item.count} x {currencyFormat(item.price)}
                      <button
                        className="cart-btn"
                        onClick={() => this.props.removeCart(item)}
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
        {cartItems.length === 0 ? (
          false
        ) : (
          <div className="total">
            <div className="total-price">
              <h2>
                Total :{" "}
                {currencyFormat(
                  cartItems.reduce(
                    (totalVal, currentVal) =>
                      totalVal + currentVal.price * currentVal.count,
                    0
                  )
                )}
              </h2>
            </div>
            <div className="divider"></div>
            <div>
              <button
                onClick={() => this.setState({ showProceedBtn: true })}
                className="btn-proceed"
              >
                {" "}
                Proceed
              </button>
            </div>
          </div>
        )}
        {this.state.showProceedBtn && (
          <div className="form">
            <form onSubmit={this.handleSubmitOrder}>
              <input
                type="text"
                name="name"
                id="name"
                placeholder="Your name"
                onChange={this.handleChange}
                value={this.state.name}
              />{" "}
              <br />
              <input
                type="email"
                name="email"
                id="email"
                placeholder="Your email"
                onChange={this.handleChange}
                value={this.state.email}
              />{" "}
              <br />
              <input
                type="text"
                name="address"
                id="address"
                placeholder="Your address"
                onChange={this.handleChange}
                value={this.state.address}
              />{" "}
              <br />
              <br />
              <br />
              <button className="btn-proceed left" type="submit">
                CheckOut
              </button>
            </form>
          </div>
        )}
      </div>
    );
  }
}
