import React, { Component } from "react";
// feature-1
import "./App.css";
import productData from "./productData.json";
import Products from "./components/Products";
import Filter from "./components/Filter";
class App extends Component {
  constructor() {
    super();
    this.state = {
      products: productData.product,
      size: "",
      sort: "",
    };
  }

  productFilter = (event) => {
    if (event.target.value === "") {
      this.setState({
        size: event.target.value,
        products: productData.product,
      });
    } else {
      this.setState({
        size: event.target.value,
        products: productData.product.filter(
          (projekt) => projekt.availableSizes.indexOf(event.target.value) >= 0
        ),
      });
    }
  };

  productOrder = (event) => {
    console.log(event.target.value);
  };

  render() {
    return (
      <div className="grid-container">
        <header>
          <h3>React Shopping Cart</h3>
        </header>
        <main>
          <div className="content">
            <div className="main-content">
              <Filter
                count={this.state.products.length}
                size={this.state.size}
                sort={this.state.sort}
                productFilter={this.productFilter}
                productOrder={this.productOrder}
              />
              <Products products={this.state.products} />
            </div>
            <div className="sideBar">SideBars</div>
          </div>
        </main>
        <footer>
          <h4>All is reserved </h4>
        </footer>
      </div>
    );
  }
}
export default App;
